import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API = process.env.REACT_APP_API_URL || 'http://localhost:8080/api';

export default function CourseCrud() {
  const [courses, setCourses] = useState([]);
  const [persons, setPersons] = useState([]);
  const [form, setForm] = useState({ id: null, name: '', teacherId: '', studentIds: [] });
  const [error, setError] = useState(null);

  useEffect(() => { fetchAll(); fetchPersons(); }, []);

  function fetchAll() {
    axios.get(`${API}/courses`).then(r => setCourses(r.data)).catch(e => setError(e.toString()));
  }

  function fetchPersons() {
    axios.get(`${API}/persons`).then(r => setPersons(r.data)).catch(e => setError(e.toString()));
  }

  function submit(e) {
    e.preventDefault();
    setError(null);
    const payload = {
      name: form.name,
      teacherId: form.teacherId || null,
      studentIds: form.studentIds || []
    };

    if (form.id) {
      axios.put(`${API}/courses/${form.id}`, payload)
        .then(() => { resetForm(); fetchAll(); })
        .catch(err => setError(err?.response?.data || err.message));
    } else {
      axios.post(`${API}/courses`, payload)
        .then(() => { resetForm(); fetchAll(); })
        .catch(err => setError(err?.response?.data || err.message));
    }
  }

  function edit(c) {
    setForm({
      id: c.id,
      name: c.name || '',
      teacherId: c.teacher ? c.teacher.id : '',
      studentIds: (c.students || []).map(s => s.id)
    });
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }

  function remove(id) {
    if (!window.confirm('Delete course?')) return;
    axios.delete(`${API}/courses/${id}`).then(() => fetchAll()).catch(e => setError(e.toString()));
  }

  function toggleStudent(studentId) {
    const set = new Set(form.studentIds || []);
    if (set.has(studentId)) set.delete(studentId); else set.add(studentId);
    setForm({ ...form, studentIds: Array.from(set) });
  }

  function resetForm() {
    setForm({ id: null, name: '', teacherId: '', studentIds: [] });
    setError(null);
  }

  return (
    <div>
      <h2>Courses</h2>

      <form onSubmit={submit} style={{ marginBottom: 12 }}>
        <input placeholder="Course name" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} />

        <div style={{ marginTop: 8 }}>
          <label>Teacher: </label>
          <select value={form.teacherId || ''} onChange={e => setForm({ ...form, teacherId: e.target.value })}>
            <option value="">-- select teacher --</option>
            {persons.map(p => (
              <option key={p.id} value={p.id}>{p.firstName} {p.lastName}</option>
            ))}
          </select>
        </div>

        <div style={{ marginTop: 8 }}>
          <label>Students:</label>
          <div style={{ maxHeight: 120, overflowY: 'auto', border: '1px solid #ddd', padding: 8 }}>
            {persons.map(p => (
              <div key={p.id}>
                <label>
                  <input type="checkbox" checked={(form.studentIds || []).includes(p.id)} onChange={() => toggleStudent(p.id)} /> {p.firstName} {p.lastName}
                </label>
              </div>
            ))}
          </div>
        </div>

        <div style={{ marginTop: 8 }}>
          <button type="submit">{form.id ? 'Update' : 'Create'}</button>
          <button type="button" onClick={resetForm} style={{ marginLeft: 8 }}>Clear</button>
        </div>
        {error && <div style={{ color: 'red', marginTop: 8 }}>{String(error)}</div>}
      </form>

      <ul>
        {courses.map(c => (
          <li key={c.id} style={{ marginBottom: 6 }}>
            <strong>{c.name}</strong> — Teacher: {c.teacher ? `${c.teacher.firstName} ${c.teacher.lastName}` : 'n/a'} — Students: {(c.students || []).map(s => `${s.firstName} ${s.lastName}`).join(', ')}
            <div>
              <button onClick={() => edit(c)}>Edit</button>
              <button onClick={() => remove(c.id)} style={{ marginLeft: 8 }}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
