import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API = process.env.REACT_APP_API_URL || 'http://localhost:8080/api';

export default function PersonCrud() {
  const [persons, setPersons] = useState([]);
  const [form, setForm] = useState({ firstName: '', lastName: '', email: '' });

  useEffect(() => { fetchAll(); }, []);

  function fetchAll() {
    axios.get(`${API}/persons`).then(r => setPersons(r.data)).catch(console.error);
  }

  function submit(e) {
    e.preventDefault();
    axios.post(`${API}/persons`, form).then(() => {
      setForm({ firstName: '', lastName: '', email: '' });
      fetchAll();
    }).catch(console.error);
  }

  function remove(id) {
    axios.delete(`${API}/persons/${id}`).then(() => fetchAll()).catch(console.error);
  }

  return (
    <div>
      <h2>Persons</h2>
      <form onSubmit={submit} style={{ marginBottom: 12 }}>
        <input placeholder="First name" value={form.firstName} onChange={e => setForm({ ...form, firstName: e.target.value })} />
        <input placeholder="Last name" value={form.lastName} onChange={e => setForm({ ...form, lastName: e.target.value })} />
        <input placeholder="Email" value={form.email} onChange={e => setForm({ ...form, email: e.target.value })} />
        <button type="submit">Create</button>
      </form>

      <ul>
        {persons.map(p => (
          <li key={p.id}>{p.firstName} {p.lastName} ({p.email}) <button onClick={() => remove(p.id)}>Delete</button></li>
        ))}
      </ul>
    </div>
  );
}
